const AJV = require('ajv'),
  path = require('path'),
  fs = require('fs-extra'),
  r2 = require('r2'),
  betterAjvErrors = require('better-ajv-errors').default;

const ajv = new AJV({
  jsonPointers: true,
  allErrors: true
});

const args = process.argv.filter((element, index) => {
  return index >= 2;
});
if (args.length != 2) {
  const usage = `
  ==============================================================
    Simplified manifest schema validator, using ajv,
    that can be used for very quick schema validations.
    This will validate a json file against the schema
    found in manifest-schema.json

    Examples:
    Usage: node manifestTester.js someManifestFile.json PATH/TO/SCHEMA/FILE.json
    Usage: node manifestTester.js someManifestFile.json http://www.PATH.TO/SCHEMA/FILE.json

    NOTE: for a graphical JSON schema validator go to
    https://www.jsonschemavalidator.net/

  ==============================================================`;
  console.log(usage);
  process.exit(1);
}

async function runValidation() {
  let schema;
  if (args[1].includes("http")) {
    schema = await r2(args[1]).json;
  }
  else {
    schema = require(path.join(__dirname, args[1]));
  }
  // console.log(`the schema is : ${JSON.stringify(schema)}`)
  const filePath = path.normalize(args[0]);
  console.log(`Retrieving ${filePath}`);
  try {
    const manifest = await fs.readFile(filePath, 'utf8');
    console.log('Converting to JSON object');
    const data = JSON.parse(manifest);

    console.log('Initializing AJV with schema');
    ajv.addSchema(schema, 'manifestSchema');
    console.log(`Validating ${filePath} against the schema`);
    var valid = ajv.validate('manifestSchema', data);
    if (!valid) {
      console.error('❌  Validation Failed');
      const output = betterAjvErrors(schema, data, ajv.errors, { format: 'js' });
      console.log(output);
      process.exit(1);

    }
    else {
      console.log('✅  Validation passed');
    }
  }
  catch (error) {
    console.error(error);
    process.exit(1);
  }
}
try {
  runValidation();
}
catch (error) {
  console.error(`❌  Error occurred running the validator ${error}`);
  process.exit(1);
}

const path = require('path');
const fs = require('fs');

const files = ['package.json', 'manifest.json', 'README.md'];
let success = true;

// Read in GitLab project ID, project URL, and branch name
const PID = process.argv[2];
const gitlabURL = process.argv[3];
const gitlabBranch = process.argv[4];
if (PID === null || PID == undefined || Number.isNaN(parseInt(PID, 10))) {
  console.error('Please run this script with Gitlab Project ID (integer) as an argument');
  process.exit(2);
}
console.log(`Generating artifact.json file w/ ${PID} as GitLab Project ID`);

const filePath = __dirname.replace("/scripts", "");
try {
  const [packageJSON, manifest, readme] = files.map(entry => {
    const result = fs.readFileSync(path.join(filePath, `./${entry}`), {
      encoding: 'utf-8'
    });

    if (entry !== 'README.md') {
        const parsedRes = JSON.parse(result);
        if (entry === 'manifest.json' && Object.prototype.hasOwnProperty.call(parsedRes, 'version')) {
          delete parsedRes.version;
        }
      return parsedRes;
    } else {
      // Update links to relative path of GitLab project in README
      return result.replace(/\.\//gm, `${gitlabURL}/-/raw/${gitlabBranch}/`);
    }
  });

  const bundles = [];

  // Read IAP component locations from manifest and combine IAP component
  // data into bundles field for artifact.json file
  const components = manifest.artifacts;
  components.forEach(file => {
    try {
      const data = fs.readFileSync(
        path.join(filePath, `./${file.location}`),
        { encoding: 'utf-8' }
      );
      bundles.push({ type: file.type, data: JSON.parse(data) });
      console.log(`     ⚪ (${file.type})   -   ${file.location}   -   ✅`);
    } catch (err) {
      success = false;
      console.log(`     ⚪  (${file.type})   -   ${file.location}   -   ❌  - ${err}`);
    }
  });

  // Create metadata of artifact.json from package.json of project
  const metadata = {
    name: packageJSON.name,
    version: packageJSON.version,
    description: packageJSON.description,
    license: packageJSON.license,
    repository: packageJSON.repository,
    keywords: packageJSON.keywords,
    author: packageJSON.author,
    IAPDependencies: packageJSON.IAPDependencies,
    gitlabId: parseInt(PID, 10),
  };

  if (success) {
    console.log(`\n\nFinished successfully`);
  } else {
    console.log(`\n\nFinished with error(s)`);
    process.exit(1);
  }

  // Combine metadata, manifest, bundles, and README into artifact.json file
  const artifact = { metadata, manifest, bundles, readme };
  fs.writeFileSync(
    path.join(filePath, './artifact.json'),
    JSON.stringify(artifact, null, 2)
  );
} catch (err) {
  console.error(`Failed to generate artifact.json file: ${err}`);
  process.exit(1);
}

