data.template:
{%set finalOutput = {} %}
{%for file in artifact%}
{%if file.content is string%}
{%set _ = finalOutput.update({file.name: file.content}) %}
{%else%}
{%for file1 in file.content%}
{%if file1.content is string%}
{%set _ = finalOutput.update({file.name + "/" + file1.name: file1.content}) %}
{%else%}
{%for file2 in file1.content%}
{%set _ = finalOutput.update({file.name + "/" + file1.name + "/" + file2.name: file2.content}) %}
{%endfor%}
{%endif%}
{%endfor%}
{%endif%}
{%endfor%}
{%for file in scripts["scriptFiles"]%}
{%set _ = finalOutput.update({"scripts/" + file.name: file.content}) %}
{%endfor%}
{%for file in scripts["testFiles"]%}
{%set _ = finalOutput.update({"test/" + file.name: file.content}) %}
{%endfor%}
{%for file in scripts["yml"]%}
{%set _ = finalOutput.update({file.name: file.content}) %}
{%endfor%}

{%set _ = finalOutput.update({"branch": branch}) %}
{%set _ = finalOutput.update({"message": commitMessage}) %}

{% for key, value in finalOutput.items() %}
{%if key in deleteFiles%}
{%set _ = deleteFiles.remove(key)%}
{%endif%}
{% endfor %}

{"add": {{finalOutput | tojson }},
"delete":{{deleteFiles | tojson}}
}

