                  "content": "#!/bin/bash\n\n# import the artifact\nARTIFACT=$(cat artifact.json)\n# echo $ARTIFACT\n\n# generate IAP token\nTOKEN=$(curl --silent --location --request POST 'http://localhost:3000/login' \\\n--header 'Content-Type: application/json' \\\n--data-raw '{\n    \"user\":{\n        \"username\" : \"admin@pronghorn\",\n        \"password\" : \"admin\"\n    }\n}')\n\nnode createPayload\n\nCREATE_RESP=$(curl --silent --location --request POST 'http://localhost:3000/prebuilts/import' \\\n--header 'Content-Type: application/json' \\\n--header \"Cookie: token=${TOKEN}\" \\\n--data-binary \"@payload.json\")\necho $CREATE_RESP\n\n# if prebuild already exists, update existing\nif [[ $CREATE_RESP == *\"already exists\"* ]]; then\n  PREBUILT=$(curl --silent --location --request GET 'http://localhost:3000/prebuilts?equals=@dsup/jennprebuilt&equalsField=name' \\\n  --header \"Cookie: token=${TOKEN}\" \\\n  --data-raw '')\n  # echo $PREBUILT\n  echo $(grep '(?<=\"name\": \")(.+)(?=\",)' artifact.json)\n  echo $(grep '/(\"name\": \".+\"){1}/' artifact.json)\n  # node extractName $PREBUILT\nfi\nrm payload.json\n# # # get prebuilts\n# # PREBUILTS=$(curl --silent --location --request GET 'http://localhost:3000/prebuilts?&equals=@dsup/jennprebuilt&equalsField=name' \\\n# # --header \"Cookie: token=${TOKEN}\")\n# # # echo $PREBUILTS\n"
