                  "name": "bitbucket-pipelines.yml",
                  "content": "image: atlassian/default-image:3\nservices:\n  - docker:dind\nafter_script:\n  - rm -rf ~/.ssh\npipelines:\n  default:\n    - step:\n        name: 'quality'\n        runs-on:\n          - self.hosted\n          - linux\n        image: alpine:3.15\n        allow_failure: true\n        script:\n          - npm i\n          # check node version:\n          - 'echo \"node version: $(node --version)\"'\n          # check npm version\n          - 'echo \"npm version: $(npm --version)\"'\n          - npm audit fix --force\n          - npm audit --registry=https://registry.npmjs.org\n    - step:\n        name: 'test'\n        runs-on:\n          - self.hosted\n          - linux\n        image: alpine:3.15\n        allow_failure: false\n        script:\n          - npm i\n          # check node version:\n          - 'echo \"node version: $(node --version)\"'\n          # check npm version\n          - 'echo \"npm version: $(npm --version)\"'\n          - npm run validateSchema\n\n          - npm i\n          # check node version:\n          - 'echo \"node version: $(node --version)\"'\n          # check npm version\n          - 'echo \"npm version: $(npm --version)\"'\n          - npm run validateSchemaLinks\n    - step:\n        name: 'bump'\n        runs-on:\n          - self.hosted\n          - linux\n        image: alpine:3.15\n        only:\n          - master\n        script:\n          - sh ./scripts/git_setup.sh\n          - sh ./scripts/node_setup.sh\n          - sh ./scripts/ver_bump.sh\n    - step:\n        name: 'generate'\n        runs-on:\n          - self.hosted\n          - linux\n        image: alpine:3.15\n        allow_failure: false\n        artifacts:\n          paths:\n          - artifact.json\n          expire_in: 5 mins \n        script:\n          - sh ./scripts/commit.sh\n    - step:\n        name: 'promote'\n        runs-on:\n          - self.hosted\n          - linux\n        image: python:3.10.11\n        allow_failure: false\n        only:\n          - master\n        dependencies: \n          - generate_artifact_json\n        script:\n          - pip3 install requests\n          - |\n            if ! python3 ./scripts/promote.py ./artifact.json; then\n              echo -e \"\\033[0;31mERROR: ***********************************************************************************\"\n              echo \"ERROR: There was an issue promoting artifact.json to IAP.\"\n              echo -e \"ERROR: ***********************************************************************************\\033[0m\"\n              exit 1\n            fi \n"
