;\\\\n  // });\\\\n\\\\n  const components = manifest.artifacts;\\\\n  components.forEach(file => {\\\\n    try {\\\\n      const data = fs.readFileSync(\\\\n        path.join(filePath, `./${file.location}`),\\\\n        { encoding: 'utf-8' }\\\\n      );\\\\n      bundles.push({ type: file.type, data: JSON.parse(data) });\\\\n      console.log(`     ⚪ (${file.type})   -   ${file.location}   -   ✅`);\\\\n    } catch (err) {\\\\n      success = false;\\\\n      console.log(`     ⚪  (${file.type})   -   ${file.location}   -   ❌  - ${err}`);\\\\n    }\\\\n  });\\\\n\\\\n  const metadata = {\\\\n    name: packageJSON.name,\\\\n    version: packageJSON.version,\\\\n    description: packageJSON.description,\\\\n    license: packageJSON.license,\\\\n    repository: packageJSON.repository,\\\\n    keywords: packageJSON.keywords,\\\\n    author: packageJSON.author,\\\\n    IAPDependencies: packageJSON.IAPDependencies,\\\\n  };\\\\n\\\\n  if (success) {\\\\n    console.log(`\\\\\\\\n\\\\\\\\nFinished successfully`);\\\\n  } else {\\\\n    console.log(`\\\\\\\\n\\\\\\\\nFinished with error(s)`);\\\\n    process.exit(1);\\\\n  }\\\\n  const artifact = { metadata, manifest, bundles, readme };\\\\n  fs.writeFileSync(\\\\n    path.join(filePath, './artifact.json'),\\\\n    JSON.stringify(artifact, null, 2)\\\\n  );\\\\n} catch (err) {\\\\n  console.error(`Failed to generate artifact.json file: ${err}`);\\\\n  process.exit(1);\\\\n}\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"commit.sh\\\",\\n                  \\\"content\\\": \\\"if ! sh ./scripts/git_setup.sh; then\\\\n  exit 1\\\\nfi\\\\necho \\\\\\\"BITBUCKET PROJECT ID IS - $BITBUCKET_REPO_UUID\\\\\\\"\\\\ncd scripts\\\\nGENERATE=`node generate $BITBUCKET_REPO_UUID $BITBUCKET_GIT_HTTP_ORIGIN ${BITBUCKET_BRANCH}`\\\\nif [ $? -ne 0 ]\\\\nthen \\\\n  echo $GENERATE\\\\n  exit 1\\\\nfi\\\\ncd ..\\\\ngit add artifact.json\\\\ngit diff --cached --name-only | if grep artifact.json\\\\nthen\\\\n    git commit -m \\\\\\\"AUTO-GENERATED FILE [skip ci]\\\\\\\"\\\\n    if ! git push origin \\\\\\\"${BITBUCKET_BRANCH}\\\\\\\"; then\\\\n      echo -e \\\\\\\"\\\\\\\\033[0;31mERROR: ***********************************************************************************\\\\\\\"\\\\n      echo \\\\\\\"ERROR: There was an issue pushing artifact.json.\\\\\\\"\\\\n      echo -e \\\\\\\"ERROR: ***********************************************************************************\\\\\\\\033[0m\\\\\\\"\\\\n      exit 1\\\\n    else\\\\n      echo \\\\\\\"A new generated artifact.json file has been checked in to current branch\\\\\\\"\\\\n    fi   \\\\nelse\\\\n    echo \\\\\\\"artifact.json file hasn't changed\\\\\\\"\\\\nfi\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"create_release_script.sh\\\",\\n                  \\\"content\\\": \\\"#!/bin/bash\\\\n#---------------------#\\\\n# Create release note #\\\\n#---------------------#\\\\n\\\\nNEW_VERSION=$1\\\\n\\\\ncreate_release_note() {\\\\n    # Create a changelog file if there isn't one\\\\n    if [ ! -e CHANGELOG.md ]; then\\\\n        touch CHANGELOG.md\\\\n    fi\\\\n    echo \\\\\\\"Creating release note...\\\\\\\"\\\\n\\\\n    #----------------#\\\\n    # Changelog Body #\\\\n    #----------------#\\\\n    # Get the merge request info\\\\n    CHANGELOG_BODY=\\\\\\\"$(git log --merges -1 --format=%b)\\\\\\\"\\\\n    if [ -z \\\\\\\"$CHANGELOG_BODY\\\\\\\" ]; then\\\\n        CHANGELOG_BODY=\\\\\\\"$(git log -1 --format='Bug fixes and performance improvements'%n%n'See commit '%h)\\\\\\\"\\\\n    fi\\\\n    #--------------------#\\\\n    # Write Release Note #\\\\n    #--------------------#\\\\n\\\\n    # get the current date\\\\n    DATE=$(date +%m-%d-%Y)\\\\n    # create the release note\\\\n    RELEASE_NOTE=\\\\\\\"\\\\\\\\\\\\\\\\n## $NEW_VERSION [$DATE]\\\\\\\\\\\\\\\\n\\\\\\\\\\\\\\\\n* $CHANGELOG_BODY\\\\\\\\\\\\\\\\n\\\\\\\\\\\\\\\\n---\\\\\\\\\\\\\\\\n\\\\\\\"\\\\n    # append Release Note to Top of CHANGELOG\\\\n    if ! echo -e \\\\\\\"$RELEASE_NOTE$(cat CHANGELOG.md)\\\\\\\" > CHANGELOG.md; then\\\\n        echo -e \\\\\\\"\\\\\\\\033[0;31mERROR: ***********************************************************************************\\\\\\\"\\\\n        echo \\\\\\\"ERROR: Unable to append release note information to changelog.\\\\\\\"\\\\n        echo -e \\\\\\\"ERROR: ***********************************************************************************\\\\\\\\033[0m\\\\\\\"\\\\n    else\\\\n        echo \\\\\\\"Created release note:\\\\\\\"\\\\n        echo \\\\\\\"$RELEASE_NOTE\\\\\\\"\\\\n    fi\\\\n}\\\\n\\\\ncreate_release_note \\\\\\\"$NEW_VERSION\\\\\\\"\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"deploy_artifact.sh\\\",\\n                  \\\"content\\\": \\\"#!/bin/bash\\\\n\\\\n# import the artifact\\\\nARTIFACT=$(cat artifact.json)\\\\n# echo $ARTIFACT\\\\n\\\\n# generate IAP token\\\\nTOKEN=$(curl --silent --location --request POST 'http://localhost:3000/login' \\\\\\\\\\\\n--header 'Content-Type: application/json' \\\\\\\\\\\\n--data-raw '{\\\\n    \\\\\\\"user\\\\\\\":{\\\\n        \\\\\\\"username\\\\\\\" : \\\\\\\"admin@pronghorn\\\\\\\",\\\\n        \\\\\\\"password\\\\\\\" : \\\\\\\"admin\\\\\\\"\\\\n    }\\\\n}')\\\\n\\\\nnode createPayload\\\\n\\\\nCREATE_RESP=$(curl --silent --location --request POST 'http://localhost:3000/prebuilts/import' \\\\\\\\\\\\n--header 'Content-Type: application/json' \\\\\\\\\\\\n--header \\\\\\\"Cookie: token=${TOKEN}\\\\\\\" \\\\\\\\\\\\n--data-binary \\\\\\\"@payload.json\\\\\\\")\\\\necho $CREATE_RESP\\\\n\\\\n# if prebuild already exists, update existing\\\\nif [[ $CREATE_RESP == *\\\\\\\"already exists\\\\\\\"* ]]; then\\\\n  PREBUILT=$(curl --silent --location --request GET 'http://localhost:3000/prebuilts?equals=@dsup/jennprebuilt&equalsField=name' \\\\\\\\\\\\n  --header \\\\\\\"Cookie: token=${TOKEN}\\\\\\\" \\\\\\\\\\\\n  --data-raw '')\\\\n  # echo $PREBUILT\\\\n  echo $(grep '(?<=\\\\\\\"name\\\\\\\": \\\\\\\")(.+)(?=\\\\\\\",)' artifact.json)\\\\n  echo $(grep '/(\\\\\\\"name\\\\\\\": \\\\\\\".+\\\\\\\"){1}/' artifact.json)\\\\n  # node extractName $PREBUILT\\\\nfi\\\\nrm payload.json\\\\n# # # get prebuilts\\\\n# # PREBUILTS=$(curl --silent --location --request GET 'http://localhost:3000/prebuilts?&equals=@dsup/jennprebuilt&equalsField=name' \\\\\\\\\\\\n# # --header \\\\\\\"Cookie: token=${TOKEN}\\\\\\\")\\\\n# # # echo $PREBUILTS\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"git_setup.sh\\\",\\n                  \\\"content\\\": \\\"#!/bin/bash\\\\n\\\\n#-----------#\\\\n# Git Setup #\\\\n#-----------#\\\\necho \\\\\\\"Setting up git credentials\\\\\\\"\\\\nmkdir -p ~/.ssh\\\\n\\\\ngit config --global user.email \\\\\\\"$CI_GIT_EMAIL\\\\\\\"\\\\ngit config --global user.name \\\\\\\"$CI_GIT_USERNAME\\\\\\\"\\\\necho -e $ID_RSA > ~/.ssh/id_rsa\\\\nchmod 0400 ~/.ssh/id_rsa\\\\n\\\\necho git config --list --show-origin\\\\necho cat ~/.ssh/id_rsa\\\\n\\\\necho -e \\\\\\\"Host bitbucket.com\\\\\\\\\\\\\\\\n\\\\\\\\\\\\\\\\tStrictHostKeyChecking no\\\\\\\\\\\\\\\\n\\\\\\\\\\\\\\\\tLogLevel ERROR\\\\\\\\\\\\\\\\n\\\\\\\" >> ~/.ssh/config\\\\ngit remote set-url origin http://bitbucket.org/$BITBUCKET_REPO_FULL_NAME\\\\ngit remote -v\\\\necho \\\\\\\"Git credentials configured.\\\\\\\"\\\\ngit stash\\\\ngit checkout ${BITBUCKET_BRANCH}\\\\nif ! git pull --rebase --tags; then \\\\n    echo -e \\\\\\\"\\\\\\\\033[0;31mERROR: ***********************************************************************************\\\\\\\"\\\\n    echo \\\\\\\"ERROR: There was an issue connecting to remote. Check your credentials.\\\\\\\"\\\\n    echo -e \\\\\\\"ERROR: ***********************************************************************************\\\\\\\\033[0m\\\\\\\"\\\\n    exit 1\\\\nfi \\\\ngit reset --hard origin/${BITBUCKET_BRANCH}\\\\n\\\\necho \\\\\\\"${BITBUCKET_BRANCH} checked out.\\\\\\\"\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"node_setup.sh\\\",\\n                  \\\"content\\\": \\\"#!/bin/bash\\\\n\\\\n# check node version:\\\\necho \\\\\\\"node version: $(node --version)\\\\\\\"\\\\necho \\\\\\\"npm version: $(npm --version)\\\\\\\"\\\\n\\\\necho \\\\\\\"Installing dependencies...\\\\\\\"\\\\nif [ ! -f package-lock.json ]; then\\\\n    PRESENT='false'\\\\n    echo 'package-lock does not exist - generating one now.'\\\\n    npm i --package-lock-only\\\\nelse \\\\n    if ! npm ci -q --unsafe-perm; then\\\\n        exit 1\\\\n    fi\\\\nfi\\\\n\\\\nif [ -n \\\\\\\"$PRESENT\\\\\\\" ]; then\\\\n    git add package-lock.json\\\\n    git commit -m 'initialized package-lock.json [ci skip]'\\\\nfi\\\\n\\\\necho \\\\\\\"Dependencies installed.\\\\\\\"\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"promote.py\\\",\\n                  \\\"content\\\": \\\"import requests\\\\nimport json\\\\nimport sys\\\\nimport os\\\\n\\\\n\\\\nhostname = os.environ.get(\\\\\\\"IAP_HOSTNAME\\\\\\\")\\\\nartifact_path = sys.argv[1]\\\\n\\\\n# checking if IAP_PUSH_TO_LOCAL env var is set -> if not, default to True\\\\nif not (os.environ.get(\\\\\\\"IAP_PUSH_TO_LOCAL\\\\\\\")):\\\\n  push_to_local = True\\\\nelse:\\\\n  push_to_local = True if os.environ.get(\\\\\\\"IAP_PUSH_TO_LOCAL\\\\\\\").lower() == \\\\\\\"true\\\\\\\" else False\\\\n\\\\n# checking if IAP_TOKEN is set -> if not, use basic auth login\\\\ntoken = os.environ.get(\\\\\\\"IAP_TOKEN\\\\\\\")\\\\nbasic_auth = False\\\\nif not token:\\\\n  basic_auth = True\\\\n  username = os.environ.get(\\\\\\\"IAP_USERNAME\\\\\\\")\\\\n  pw = os.environ.get(\\\\\\\"IAP_PW\\\\\\\")\\\\n\\\\nif not (artifact_path and hostname and (token or (username and pw))):\\\\n  print(\\\\\\\"Missing environmental variables.\\\\\\\\nMake sure your environmental variables are set properly.\\\\\\\\nExiting...\\\\\\\")\\\\n  sys.exit(1)\\\\n\\\\nartifact = json.load(open(f\\\\\\\"{artifact_path}\\\\\\\"))\\\\n# Function Definitions\\\\n# Handles getting token to authenticate into IAP\\\\ndef get_token():\\\\n  print(\\\\\\\"Getting auth token\\\\\\\")\\\\n  url = f\\\\\\\"{hostname}/login\\\\\\\"\\\\n  payload = json.dumps({\\\\n    \\\\\\\"user\\\\\\\": {\\\\n      \\\\\\\"username\\\\\\\": username,\\\\n      \\\\\\\"password\\\\\\\": pw\\\\n    }\\\\n  })\\\\n\\\\n  response = requests.request(\\\\n    \\\\\\\"POST\\\\\\\", \\\\n    url, \\\\n    headers={'Content-Type': 'application/json'},\\\\n    data=payload)\\\\n  if not response.status_code // 100 == 2:\\\\n    raise Exception(\\\\\\\"Error: Unexpected response {}: Failed to get auth token\\\\\\\".format(response.text))\\\\n  else: \\\\n    return response.text\\\\n\\\\n# Checks if prebuilt already exists\\\\ndef get_prebuilt(name):\\\\n  print(f\\\\\\\"Retrieving prebuilt: {name}\\\\\\\")\\\\n  url = f\\\\\\\"{hostname}/prebuilts?equals={name}&equalsField=name\\\\\\\"\\\\n  response = requests.request(\\\\n    \\\\\\\"GET\\\\\\\", \\\\n    url,\\\\n    headers={'Cookie': f'token={token}'})\\\\n  if not response.status_code // 100 == 2:\\\\n    raise Exception(\\\\\\\"Error: Unexpected response {}: Failed to get prebuilt\\\\\\\".format(response.text))\\\\n  else: \\\\n    return response.text\\\\n\\\\ndef add_prebuilt(payload): \\\\n  print(\\\\\\\"Prebuilt does not exist yet - adding to IAP\\\\\\\")\\\\n  url = f\\\\\\\"{hostname}/prebuilts/import\\\\\\\"\\\\n  headers = {\\\\n    'Content-Type': 'application/json',\\\\n    'Cookie': f'token={token}'\\\\n  }\\\\n  response = requests.request(\\\\n    \\\\\\\"POST\\\\\\\",\\\\n    url,\\\\n    headers=headers,\\\\n    data=payload)\\\\n  if \\\\\\\"Invalid repository configuration\\\\\\\" in response.text:\\\\n    print(\\\\\\\"Failed to promote to original repository, pushing to local scope.\\\\\\\")\\\\n    updated_payload = json.loads(payload)\\\\n    updated_payload[\\\\\\\"prebuilt\\\\\\\"][\\\\\\\"metadata\\\\\\\"][\\\\\\\"repository\\\\\\\"] = {\\\\n      \\\\\\\"type\\\\\\\": \\\\\\\"local\\\\\\\",\\\\n      \\\\\\\"hostname\\\\\\\": \\\\\\\"localhost\\\\\\\",\\\\n      \\\\\\\"path\\\\\\\": \\\\\\\"/\\\\\\\"\\\\n    }\\\\n    response = requests.request(\\\\n      \\\\\\\"POST\\\\\\\",\\\\n      url,\\\\n      headers=headers,\\\\n      data=json.dumps(updated_payload))\\\\n  if not response.status_code // 100 == 2:\\\\n    raise Exception(\\\\\\\"Error: Unexpected response {}: Failed to add prebuilt\\\\\\\".format(response.text))\\\\n  else: \\\\n    print(\\\\\\\"Successfully added prebuilt\\\\\\\")\\\\n    return response.text\\\\n\\\\ndef update_prebuilt(id, payload):\\\\n  print(\\\\\\\"Updating existing prebuilt\\\\\\\")\\\\n  url = f\\\\\\\"{hostname}/prebuilts/{id}\\\\\\\"\\\\n  headers = {\\\\n    'Content-Type': 'application/json',\\\\n    'Cookie': f'token={token}'\\\\n  }\\\\n  response = requests.request(\\\\n    \\\\\\\"PUT\\\\\\\",\\\\n    url,\\\\n    headers=headers,\\\\n    data=payload)\\\\n  if \\\\\\\"Invalid repository configuration\\\\\\\" in response.text:\\\\n    print(\\\\\\\"Failed to promote to original repository, pushing to local scope.\\\\\\\")\\\\n    updated_payload = json.loads(payload)\\\\n    updated_payload[\\\\\\\"prebuilt\\\\\\\"][\\\\\\\"metadata\\\\\\\"][\\\\\\\"repository\\\\\\\"] = {\\\\n      \\\\\\\"type\\\\\\\": \\\\\\\"local\\\\\\\",\\\\n      \\\\\\\"hostname\\\\\\\": \\\\\\\"localhost\\\\\\\",\\\\n      \\\\\\\"path\\\\\\\": \\\\\\\"/\\\\\\\"\\\\n    }\\\\n    response = requests.request(\\\\n      \\\\\\\"PUT\\\\\\\",\\\\n      url,\\\\n      headers=headers,\\\\n      data=json.dumps(updated_payload))\\\\n  if not response.status_code // 100 == 2:\\\\n      raise Exception(\\\\\\\"Error: Unexpected response {}: Failed to update prebuilt\\\\\\\".format(response.text))\\\\n  else: \\\\n    print(\\\\\\\"Successfully updated prebuilt\\\\\\\")\\\\n    return response.text\\\\n\\\\ndef logout():\\\\n  print(\\\\\\\"Logging out of IAP\\\\\\\")\\\\n  url = f\\\\\\\"{hostname}/login?logout=true\\\\\\\"\\\\n  headers = {\\\\n    'Content-Type': 'application/json',\\\\n    'Cookie': f'token={token}'\\\\n  }\\\\n  response = requests.request(\\\\n    \\\\\\\"GET\\\\\\\",\\\\n    url,\\\\n    headers=headers)\\\\n  response = requests.request(\\\\\\\"GET\\\\\\\", url, headers=headers)\\\\n\\\\n# Script starts here\\\\ntry: \\\\n  if (basic_auth):\\\\n    token = get_token()\\\\n\\\\n  # Set name of prebuilt\\\\n  name = artifact[\\\\\\\"metadata\\\\\\\"][\\\\\\\"name\\\\\\\"]\\\\n\\\\n  results = get_prebuilt(name)\\\\n  if push_to_local:\\\\n    print(\\\\\\\"Setting artifact.json repository configuration to local\\\\\\\")\\\\n    artifact[\\\\\\\"metadata\\\\\\\"][\\\\\\\"repository\\\\\\\"] = {\\\\n      \\\\\\\"type\\\\\\\": \\\\\\\"local\\\\\\\",\\\\n      \\\\\\\"hostname\\\\\\\": \\\\\\\"localhost\\\\\\\",\\\\n      \\\\\\\"path\\\\\\\": \\\\\\\"/\\\\\\\"\\\\n    }\\\\n    print('Promoting to local scope in IAP')\\\\n  payload = json.dumps({\\\\n    \\\\\\\"prebuilt\\\\\\\": artifact,\\\\n    \\\\\\\"options\\\\\\\": {\\\\n      \\\\\\\"overwrite\\\\\\\": True \\\\n    }\\\\n  })\\\\n  # if prebuilt doesn't exist, add it\\\\n  if json.loads(results)[\\\\\\\"total\\\\\\\"] == 0:\\\\n    response = add_prebuilt(payload)\\\\n  else: # if prebuilt exists, update it\\\\n      id = json.loads(results)[\\\\\\\"results\\\\\\\"][0][\\\\\\\"_id\\\\\\\"]\\\\n      update_prebuilt(id, payload)\\\\n\\\\n  # logging out\\\\n  logout()\\\\nexcept requests.exceptions.RequestException as e: \\\\n  # A serious problem happened, like an SSLError or InvalidURL\\\\n  print(\\\\\\\"Error: {}\\\\\\\".format(e))\\\\n  sys.exit(1)  \\\\nexcept: # error handling to catch any errors that throw a non 200 code\\\\n  e = sys.exc_info()[1]    \\\\n  print(e)\\\\n  sys.exit(1)\\\\n\\\"\\n                },\\n                {\\n                  \\\"name\\\": \\\"ver_bump.sh\\\",\\n                  \\\"content\\\": \\\"#!/bin/bash\\\\n\\\\n#-------------------#\\\\n# Get Semver Prefix #\\\\n#-------------------#\\\\nVERSION=\\\\\\\"$(git log --format=%s --merges -1|awk -F\\\\\\\"'\\\\\\\" '{print $2}'|awk -F \\\\\\\"/\\\\\\\" '{print $1}')\\\\\\\"\\\\ncase \\\\\\\"$VERSION\\\\\\\" in\\\\n    # if PATCH transform to patch\\\\n    patch|minor|major) SEMVER=\\\\\\\"$(echo \\\\\\\"$VERSION\\\\\\\" | awk '{print tolower($0)}')\\\\\\\";;\\\\n    *) SEMVER=\\\\\\\"patch\\\\\\\";;\\\\nesac\\\\nif [ -z \\\\\\\"$VERSION\\\\\\\" ]; then\\\\n    echo \\\\\\\"No branch prefix detected. Defaulting to patch.\\\\\\\"\\\\nfi\\\\n\\\\necho \\\\\\\"Semver bump: $SEMVER\\\\\\\"\\\\n\\\\n#---------------------#\\\\n# Get Current Version #\\\\n#---------------------#\\\\nCURRENT_VERSION=$(node -p \\\\\\\"require('./package.json').version\\\\\\\")\\\\necho \\\\\\\"Last version: $CURRENT_VERSION\\\\\\\"\\\\nCURRENT_MAJOR=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'.' '{print $1}')\\\\nCURRENT_MINOR=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'.' '{print $2}')\\\\nCURRENT_PATCH=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'.' '{print $3}'|awk -F'-' '{print $1}')\\\\nCURRENT_PRERELEASE=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'-' '{print $2}')\\\\n\\\\n# if [[ $CURRENT_VERSION != *\\\\\\\"+\\\\\\\"* ]]; then\\\\n#     CURRENT_PATCH=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'.' '{print $3}'|awk -F'-' '{print $1}')\\\\n#     CURRENT_PRERELEASE=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'-' '{print $2}')\\\\n# else\\\\n#     CURRENT_PATCH=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'.' '{print $3}'|awk -F'+' '{print $1}')\\\\n#     CURRENT_PRERELEASE=$(echo \\\\\\\"$CURRENT_VERSION\\\\\\\"|awk -F'+' '{print $2}')\\\\n# fi \\\\n\\\\n\\\\n#---------------------------#\\\\n# Version & Release Package #\\\\n#---------------------------#\\\\n\\\\n# create release note before versioning the project\\\\nsh ./scripts/create_release_script.sh \\\\\\\"$NEW_VERSION\\\\\\\"\\\\n\\\\n# explicitly add the change log to the version bump commit\\\\ngit add CHANGELOG.md\\\\necho \\\\\\\"Bumping version...\\\\\\\"\\\\n\\\\n# bump version, git tag, commit & then push changes\\\\n# using -f to ignore the added release note\\\\nnpm version -f \\\\\\\"$SEMVER\\\\\\\" -m \\\\\\\"Updating $SEMVER version to %s. [skip ci]\\\\\\\" --loglevel=error\\\\necho \\\\\\\"Version bump successful\\\\\\\"\\\\n\\\\n# push to gitlab\\\\n# no-verify is used to ignore any pre-push commits that may be used by the project\\\\nif git push -f --tags origin ${BITBUCKET_BRANCH} --follow-tags --no-verify; then\\\\n    echo \\\\\\\"Version bump pushed successfully.\\\\\\\"\\\\nelse\\\\n    echo -e \\\\\\\"\\\\\\\\033[0;31mERROR: ***********************************************************************************\\\\\\\"\\\\n    echo \\\\\\\"ERROR: Failed to push.\\\\\\\"\\\\n    echo -e \\\\\\\"ERROR: ***********************************************************************************\\\\\\\\033[0m\\\\\\\"\\\\n    exit 1\\\\nfi\\\\n\\\"\\n                }\\n              ],\\n              \\\"testFiles\\\": [\\n                {\\n                  \\\"name\\\": \\\"manifest-schema.json\\\",\\n                  \\\"content\\\": \\\"{\\\\n  \\\\\\\"$schema\\\\\\\": \\\\\\\"http://json-schema.org/draft-07/schema#\\\\\\\",\\\\n  \\\\\\\"$id\\\\\\\": \\\\\\\"iap-artifact-bundle\\\\\\\",\\\\n  \\\\\\\"type\\\\\\\": \\\\\\\"object\\\\\\\",\\\\n  \\\\\\\"title\\\\\\\": \\\\\\\"Artifact Bundle Schema\\\\\\\",\\\\n  \\\\\\\"default\\\\\\\": null,\\\\n  \\\\\\\"additionalProperties\\\\\\\": false,\\\\n  \\\\\\\"definitions\\\\\\\": {\\\\n    \\\\\\\"tagged\\\\\\\": {\\\\n      \\\\\\\"$id\\\\\\\": \\\\\\\"#/properties/properties/properties/tagged\\\\\\\",\\\\n      \\\\\\\"type\\\\\\\": \\\\\\\"array\\\\\\\",\\\\n      \\\\\\\"title\\\\\\\": \\\\\\\"The Tagged Item\\\\\\\",\\\\n      \\\\\\\"description\\\\\\\": \\\\\\\"These are the items that need to be tagged\\\\\\\